#ifdef SAVE
#include <stdio.h>
static FILE *fh;
static void loc_save_file_open(const char *_name) {
   fh = fopen(_name, "wb");
}
static void loc_save_env(const short *_data) {
   fwrite((void*)_data, 2048 * sizeof(short), 1, fh);
}
static void loc_save_file_close(void) {
   fclose(fh);
   fh = NULL;
}
#endif // SAVE

